    <body>

      <div class="container">
        <div class="printer-top"></div>
        <div class="paper-container">
          <div class="printer-bottom"></div>
          <div class="paper">
            <div class="main-contents">
              <div class="success-icon">&#10004;</div>
              <div class="success-title">
                Pedido em análise
              </div>
              <div class="order-details">
                <div id="itemSelectedColor"></div>
                <div id="itemSelectedSize"></div>
                <!-- <div id="itemSelectedPrice"></div>-->
              </br>
                <div class="order-details-center">
                  <div id="itemSelectedPicture"></div>
                </div></br><div style="text-align:center;">Pedido Nº</div></br>
                <div id="itemSelectedOrderNumber"></div>
              </div>
            </div>
            <div class="jagged-edge"></div>
          </div>
        </div>
      </div>

    </body>
